            label="Name"
            required
            ></v-text-field>

            <v-text-field
            v-model="email"
            :rules="emailRules"
            label="E-mail"
            required
            ></v-text-field>

            <v-text-field
            v-model="phone"
            label="Phone"
            ></v-text-field>

            <v-text-field
            v-model="message"
            auto-grow
            label="Message"
            required
            ></v-text-field>

            <p class="display-1 mb-3 text-left text-xs-body-2 text-md-body-1 text-lg-body-1">This page is under construction, so chrome autofill isn't working properly. Don't worry, I'm looking for a solution :)</p>
