<script>
import axios from 'axios'
// import mixpanel from 'mixpanel-browser';
// mixpanel.track('Contact', {
//   'source': "Contact page",
//   'Contact Page Visited': true,
// });
export default {
  name: 'HelloWorld',
  title: 'Contact Me',

  data: () => ({

    blogs: [],
    snackbar: false,
    snackbarError: false,
    text: `Contact message sent! Thanks for getting in touch :)`,
    textError: `Looks like you're still missing some fields`,
    messages: [],
    images: [],
    error: null,
    valid: true,
    name: '',
    nameRules: [
    v => !!v || 'Name is required',
    v => (v && v.length <= 35) || 'Name must be less than 10 characters',
    ],
    phone: '',
    message: '',
    email: '',
    emailRules: [
    v => !!v || 'E-mail is required',
    v => /.+@.+\..+/.test(v) || 'E-mail must be valid',
    ],
    select: null,

  }),
  methods: {
      async sendMessage() {
        //   console.log("Sending POST call");
          try {
            await axios.post(`${process.env.VUE_APP_API_ENDPOINT}api/messages`, {
              data: {
                Name: this.name,
                Email: this.email,
                Phone: this.phone,
                Message: this.message,
              },
            });
            // console.log(res.data);
            
          } catch (error) {
            return alert(
              "Something went wrong with the POST call."
            );
          }
        },
      validate () {
        this.$refs.form.validate()
      },
      reset () {
        this.$refs.form.reset()
      },
      resetValidation () {
        this.$refs.form.resetValidation()
      },
    },
  async mounted () {
    try {
      const qs = require('qs');
      const query = qs.stringify(
        {
          populate: {
            Image: {
              populate: '*'
            }
          }
        },
        {
          encodeValuesOnly: true
        }
      );
      // get request
      const Response= await axios.get(
        `${process.env.VUE_APP_API_ENDPOINT}api/messages/?${query}`
      );
      console.log(Response.data.data);
      this.messages = Response.data.data

    } catch (error) {
      this.error = error;
    }
  }
}
</script>
