<template>
  <v-container>
    <v-row class="text-center">
      <!-- Introduction -->
      <v-col class="mb-4">
        <h1 class="display-2 font-weight-light mb-3 text-xs-h3 text-md-h4 text-lg-h3">
          Hi there! I'm Bryce
        </h1>


        <p class="subheading font-weight-regular text-xs-body-2 text-md-body-1 text-lg-h5">
          I'm a UX designer at Functionize and a graduate of the Computer Science program at Georgia Tech.
        </p>
      </v-col>

      <v-col
        class="mb-5"
        cols="12"
      >
        <h2 class="headline font-weight-medium mb-5 text-xs-h6 text-md-h5 text-lg-h4">
          My projects
        </h2>
    <!-- Projects -->
    <v-container fluid>
      <v-row dense>
        <v-col
          v-for="project in projects" :key="project.id"
          :cols="project.attributes.flex"
        > 
          <router-link :to="project.attributes.route" style="text-decoration: none; color: inherit;">
            <v-card v-ripple id="mixpanel-tracked">
              <v-img
                :src= "project.attributes.Image.data.attributes.caption"
                class="white--text align-end"
                gradient="to bottom, rgba(0,0,0,.1), rgba(0,0,0,.5)"
                height="400px"
                :alt= "project.attributes.Image.data.attributes.alternativeText"
                cover
              >
                <v-card-title class="text-white d-flex justify-start font-weight-light text-left" v-text="project.attributes.Name"></v-card-title>
                <v-card-text class="text-white font-weight-bold d-flex justify-start text-left" v-text="project.attributes.Role"></v-card-text>
                <v-card-text class="text-white text-left" v-text="project.attributes.Description"></v-card-text>
              </v-img>
            </v-card>
          </router-link>
        </v-col>
      </v-row>
    </v-container>
      </v-col>

    
      <!-- About Me -->
